import { useState } from 'react';
import styled from 'styled-components';

import Login from './components/Login';
import Signup from './components/Signup';

import { flex } from 'styles/flex';
import { fitImg } from 'styles/mixins';
import { font } from 'styles/fonts';

export default function Auth() {
  const [mode, setMode] = useState('로그인');
  return (
    <Wrapper>
      <LeftContainer>
        <LogoContainer>
          <img alt="logo" src="./images/ic-logo.png" />
          <p>TripTodo</p>
        </LogoContainer>
        <ModeContainer>
          {mode === '로그인' ? <Login /> : <Signup />}
        </ModeContainer>
        <Button>{mode === '로그인' ? '로그인' : '확인'} </Button>
        <ModeChangerBtn>
          {mode === '로그인'
            ? '아직 계정이 없으신가요? 회원가입'
            : '로그인하러 가기'}
        </ModeChangerBtn>
      </LeftContainer>
      <RightContainer>
        <img alt="main-background" src="/images/il-main-background.jpeg" />
      </RightContainer>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
`;

const LeftContainer = styled.div`
  width: 50%;
  height: 100%;
  padding: 40px;
  background-color: #faf8f1;
`;

const RightContainer = styled.div`
  width: 50%;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const LogoContainer = styled.div`
  ${flex('', 'center')}
  ${font(18, 900, 24)}
  height: 30px;
  color: #c58940;
  gap: 10px;

  p {
    letter-spacing: 10px;
  }
`;

const ModeContainer = styled.div`
  position: relative;
`;

const Button = styled.button`
  display: block;
  margin: 50px auto 0;
  width: 420px;
  ${font(16, 700, 18)};
  color: white;
  padding: 15px;
  border-radius: 10px;
  background-color: #c58940;
`;

const ModeChangerBtn = styled.button`
  ${font(14, 400, 16)}
  display: block;
  margin: 20px auto;
  color: #c58940;
`;
