import { HTMLAttributes } from 'react';
import styled, { css } from 'styled-components';

import { flex } from 'styles/flex';
import { font } from 'styles/fonts';

interface IInputPropTypes extends HTMLAttributes<HTMLInputElement> {
  autoFocus?: boolean;
  isError?: boolean;
  subText?: string;
  title: string;
  type?: string;
  value?: string;
  name?: string;
  placeholder?: string;
  onChangeInput: Function;
  isSuccess?: boolean;
  isFormatError?: boolean;
  formatErrorText?: string;
  marginBottom?: number;
}

function Input({
  autoFocus,
  isError,
  subText,
  title,
  type,
  value,
  onChangeInput,
  name,
  placeholder,
  isSuccess,
  isFormatError,
  formatErrorText,
  marginBottom,
}: IInputPropTypes) {
  return (
    <InputWrapper
      isError={isError || isFormatError}
      marginBottom={marginBottom}
    >
      <InputText isError={isError || isFormatError}>
        <p>{title}</p>
      </InputText>
      <InputField
        autoFocus={autoFocus}
        isError={isError}
        name={name}
        placeholder={placeholder}
        type={type}
        value={value}
        onChange={(e) => onChangeInput(e)}
      />
      {(isError || isSuccess) && (
        <InputSubText isError={isError} isSuccess={isSuccess}>
          {subText}
        </InputSubText>
      )}
      {isFormatError && (
        <InputSubText isError={isFormatError}>{formatErrorText}</InputSubText>
      )}
    </InputWrapper>
  );
}

export default Input;

interface IInputStyleProps extends HTMLAttributes<HTMLInputElement> {
  isError?: boolean;
  isSuccess?: boolean;
  marginBottom?: number;
}

const InputWrapper = styled.div<IInputStyleProps>`
  position: relative;
  width: 100%;
  height: 50px;
  margin-bottom: ${({ marginBottom }) => marginBottom || 30}px;
  ${flex('center', 'center', 'row')};
  border: 1px solid #c0c0c0;
  border-radius: 10px;

  ${({ isError }) =>
    isError &&
    css`
      border: 1px solid #ea3313;
    `};
`;

const InputText = styled.div<IInputStyleProps>`
  ${font(14, 400, 16)};
  min-width: 78px;
  margin-left: 15px;

  p {
    color: #15aedb;

    ${({ isError }) =>
      isError &&
      css`
        color: #ea3313;
      `};
  }
`;

const InputField = styled.input<IInputStyleProps>`
  ${font(16, 400, 18)};
  width: 100%;
  height: 56px;
  padding-left: 10px;
  color: #000000;

  ${({ isError }) =>
    isError &&
    css`
      color: #ea3313;
    `};

  ${({ type }) =>
    type === 'password' &&
    css`
      font-family: sans-serif;
    `};

  ::placeholder {
    ${font(16, 400, 18)};
    color: #999999;
  }
`;

const InputSubText = styled.div<IInputStyleProps>`
  ${font(12, 400, 14)};
  position: absolute;
  bottom: -24px;
  left: 12px;
  color: #999999;

  ${({ isError }) =>
    isError &&
    css`
      color: #ea3313;
    `};

  ${({ isSuccess }) =>
    isSuccess &&
    css`
      color: #15aedb;
    `};
`;
