import { useMutation } from 'react-query';
import axios, { AxiosError } from 'axios';

import axiosClient from 'libs/axios/axios';

interface ICreateTodo {
  title: string;
  content: string;
}

const fetchTodos = async (body: ICreateTodo) => {
  const { title, content } = body;
  const { data } = await axiosClient.post('todos', {
    title,
    content,
  });
  return data;
};

const useCreateTodoQuery = () =>
  useMutation(['todos', 'state'], fetchTodos, {
    onSuccess: (data) => data,
    onError: (error: AxiosError) => {
      console.error(`createTodoError : ${error}`);
    },
  });

export default useCreateTodoQuery;
