import { useState } from 'react';
import styled from 'styled-components';
import { flex } from 'styles/flex';
import { font } from 'styles/fonts';

export default function TodoDetailContainer() {
  const [editMode, setEditMode] = useState(true);

  return (
    <Container>
      <TitleWrap>
        {editMode ? <Input /> : <Title>제목</Title>}
        <div>
          <EditBtn>수정</EditBtn>
          <DeleteBtn>삭제</DeleteBtn>
        </div>
      </TitleWrap>
      <ContentWrap>
        {editMode ? <TextArea /> : <Content>작성내용</Content>}
      </ContentWrap>
      <DetailWrap>
        <DetailText>최초 작성일자 : 2022-11-20</DetailText>
        <DetailText>마지막 수정일자 : 2022-11-20</DetailText>
      </DetailWrap>
    </Container>
  );
}

const Container = styled.div`
  width: 60%;
  padding: 40px;
  overflow: hidden;
  border-radius: 10px;
  background-color: #ffffff;
`;

const TitleWrap = styled.div`
  ${flex('space-between', 'center')}
  width: 100%;
`;

const Input = styled.input`
  ${font(24, 700, 30)}
`;

const Title = styled.h1`
  ${font(24, 700, 30)}
  color: #815927;
`;

const DetailWrap = styled.div`
  ${flex('space-between', 'center')}
`;

const DetailText = styled.div`
  margin-top: 15px;
  color: gray;
`;

const ContentWrap = styled.div`
  width: 100%;
  height: 200px;
  margin-top: 15px;
`;

const Content = styled.div`
  width: 100%;
  height: 100%;
  padding: 15px;
  border-radius: 5px;
  background-color: #fbe8d2;
`;

const TextArea = styled.textarea`
  ${font(16, 400, 18)};
  width: 100%;
  height: 150px;
  padding: 15px;
  color: #c58940;
  border: 1px solid #e5ba73;
  border-radius: 10px;
  resize: none;
  font-family: 'NanumSquare';

  ::placeholder {
    ${font(14, 400, 18)};
    color: #e5ba73;
  }

  &:focus {
    outline: none;
  }
`;

const EditBtn = styled.button`
  color: white;
  padding: 6px 8px;
  border-radius: 5px;
  background-color: #c1a179;
`;

const DeleteBtn = styled(EditBtn)`
  margin-left: 10px;
`;
