import React, { useState } from 'react';
import styled from 'styled-components';
import TodoDetailContainer from './components/TodoDetailContainer';
import TodoListContainer from './components/TodoListContainer';
import useGetTodoByIdQuery from './queries/useGetTodoByIdQuery';

export default function Todo() {
  const [selectedId, setSelectedId] = useState('');
  const { data: selectedData } = useGetTodoByIdQuery(selectedId);

  const handleSelectedId = (id: string) => {
    setSelectedId(id);
  };

  return (
    <Wrapper>
      <TodoListContainer handleSelectedId={handleSelectedId} />
      <TodoDetailContainer />
    </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
  padding: 40px;
  overflow: hidden;
  background-color: #faf8f1;
  gap: 100px;
`;
